import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import Link for navigation
import "./basket.css";

const Basket = () => {
  const [basketItems, setBasketItems] = useState([
    {
      id: 1,
      name: "Royal Cheese Burger",
      description: "With extra fries",
      price: 120,
      quantity: 1,
    },
    {
      id: 2,
      name: "Potato Veggies",
      description: "No Mushrooms + green peppers",
      price: 70,
      quantity: 1,
    },
    {
      id: 3,
      name: "Coke Coca Cola",
      description: "",
      price: 40,
      quantity: 1,
    },
  ]);

  const subTotal = basketItems.reduce((total, item) => total + item.price * item.quantity, 0);
  const discount = 3;
  const deliveryFee = 3;
  const total = subTotal - discount + deliveryFee;

  const navigate = useNavigate();

  const handleCheckout = () => {
    navigate("/checkout");
  };

  // Function to handle adding items to the basket
  const handleAddToCart = (newItem) => {
    // Check if the item already exists in the basket
    const itemIndex = basketItems.findIndex((item) => item.id === newItem.id);

    if (itemIndex >= 0) {
      // If item exists, update its quantity
      const updatedItems = [...basketItems];
      updatedItems[itemIndex].quantity += 1;
      setBasketItems(updatedItems);
    } else {
      // If item doesn't exist, add it to the basket
      setBasketItems([...basketItems, { ...newItem, quantity: 1 }]);
    }
  };

  const Basket = ({ basketItems, onRemove, onUpdateQuantity }) => {
    return (
      <div className="basket">
        <h2>Your Basket</h2>
        {basketItems.length > 0 ? (
          <ul>
            {basketItems.map((item) => (
              <li key={item.id}>
                <div>
                  <strong>{item.name}</strong> - ‚Çπ{item.price} x {item.quantity}
                </div>
                <div>
                  <button onClick={() => onUpdateQuantity(item.id, item.quantity - 1)}>-</button>
                  <button onClick={() => onUpdateQuantity(item.id, item.quantity + 1)}>+</button>
                  <button onClick={() => onRemove(item.id)}>Remove</button>
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p>Your basket is empty.</p>
        )}
      </div>
    );
  };
}  

export default Basket;


























