import React, { useState } from 'react';
import './basket.css';

const App = () => {
  const [basket, setBasket] = useState([]);
  const [categories] = useState([
    {
      name: 'Burgers',
      products: [
        { id: 1, name: 'Cheeseburger', price: 5.99 },
        { id: 2, name: 'Veggie Burger', price: 4.99 },
      ],
    },
    {
      name: 'Fries',
      products: [
        { id: 3, name: 'Small Fries', price: 2.49 },
        { id: 4, name: 'Large Fries', price: 3.49 },
      ],
    },
    {
      name: 'Drinks',
      products: [
        { id: 5, name: 'Soda', price: 1.99 },
        { id: 6, name: 'Milkshake', price: 2.99 },
      ],
    },
  ]);

  // Add an item to the basket
  const addToBasket = (product) => {
    setBasket((prevBasket) => [...prevBasket, product]);
  };

  // Remove an item from the basket
  const removeFromBasket = (productId) => {
    setBasket((prevBasket) => prevBasket.filter((item, index) => index !== productId));
  };

  // Calculate total price
  const calculateTotal = () => {
    return basket.reduce((total, item) => total + item.price, 0).toFixed(2);
  };

  return (
    <div className="app">
      <header className="header">
        <h1>Online Ordering</h1>
      </header>
      <div className="content">
        {/* Product List */}
        <div className="product-list">
          <h2>Menu</h2>
          {categories.map((category) => (
            <div key={category.name} className="category">
              <h3>{category.name}</h3>
              <ul>
                {category.products.map((product) => (
                  <li key={product.id} className="product-item">
                    <span>{product.name} - ${product.price.toFixed(2)}</span>
                    <button onClick={() => addToBasket(product)}>Add to Basket</button>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>

        {/* Shopping Basket */}
        <div className="basket">
          <h2>Your Basket</h2>
          {basket.length === 0 ? (
            <p>Your basket is empty.</p>
          ) : (
            <ul>
              {basket.map((item, index) => (
                <li key={index} className="basket-item">
                  <span>{item.name} - ${item.price.toFixed(2)}</span>
                  <button onClick={() => removeFromBasket(index)}>Remove</button>
                </li>
              ))}
            </ul>
          )}
          <div className="basket-total">
            <h3>Total: ${calculateTotal()}</h3>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
