import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../header/header";
import Footer from "../footer/Footer";
import "./basket.css";

const Baskets = () => {
  const [addedProducts, setAddedProducts] = useState([
    { id: 1, name: "Royal Cheese Burger", description: "With extra fries", price: 120, quantity: 1 },
    { id: 2, name: "Potato Veggies", description: "No Mushrooms + green peppers", price: 70, quantity: 1 },
    { id: 3, name: "Coke Coca Cola", description: "", price: 40, quantity: 1 },
  ]);

  const navigate = useNavigate();

  // Handle copying share link
  const handleCopyLink = () => {
    navigator.clipboard.writeText("Your link here!");
    alert("Link copied to clipboard!");
  };

  // Add item to basket
  const handleAddToCart = (newItem) => {
    const itemIndex = basketItems.findIndex((item) => item.id === newItem.id);
    if (itemIndex >= 0) {
      const updatedItems = [...basketItems];
      updatedItems[itemIndex].quantity += 1;
      setBasketItems(updatedItems);
    } else {
      setBasketItems([...basketItems, { ...newItem, quantity: 1 }]);
    }
  };

  // Remove item from basket
  const handleRemoveFromCart = (itemId) => {
    const updatedItems = basketItems.filter((item) => item.id !== itemId);
    setBasketItems(updatedItems);
  };

  const subTotal = basketItems.reduce((total, item) => total + item.price * item.quantity, 0);
  const discount = 3;
  const deliveryFee = 3;
  const total = subTotal - discount + deliveryFee;

  // Handle navigation to checkout
  const handleCheckout = () => {
    navigate("/checkout");
  };

  return (
    <div>
      <Header />

      {/* Banner Section */}
      <section className="banner1">
        <div className="banner-content1">
          <p>I'm lovin' it!</p>
          <h1>McDonald‚Äôs East London</h1>
          <div className="search-bar1">
            <div id="input_container">
              <img src="/images/Order Completed.png" alt="order completed" />
              <div>Minimum Order: 12 GBP</div>
            </div>
            <div id="input_container">
              <img src="/images/Motocross.png" alt="motocross" />
              <div>Delivery in 20-25 Minutes</div>
            </div>
          </div>
        </div>
      </section>

      {/* Menu Categories */}
      <section className="exclusive-deals1">
        <ul className="bar-links1">
          <li><a href="#offers">Offers</a></li>
          <li><a href="#burgers">Burgers</a></li>
          <li><a href="#fries">Fries</a></li>
          <li><a href="#drinks">Cool Drinks</a></li>
        </ul>
      </section>

      {/* Products Section */}
      <section>
        <h3>Burgers</h3>
        <div className="category-cards">
          {basketItems.map((item) => (
            <div className="product-card" key={item.id}>
              <div className="content">
                <h2>{item.name}</h2>
                <p>{item.description}</p>
                <h2>‚Çπ {item.price}</h2>
              </div>
              <div className="images">
                <img src={`/images/${item.name}.png`} alt={item.name} />
                <button onClick={() => handleAddToCart(item)}>Add to Basket</button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Basket Section */}
      <div className="basket-container">
        <header className="basket-header">
          <img src="/images/Full Shopping Basket.png" alt="icon" />
          <span>My Basket</span>
        </header>
        <div className="basket-items">
          {basketItems.map((item) => (
            <div className="basket-item" key={item.id}>
              <div className="item-quantity">{item.quantity}x</div>
              <div className="item-details">
                <span className="item-name">{item.name}</span>
                <span className="item-description">{item.description}</span>
              </div>
              <div className="item-price">‚Çπ{item.price}</div>
              <button className="item-remove" onClick={() => handleRemoveFromCart(item.id)}>üóëÔ∏è</button>
            </div>
          ))}
        </div>
        <div className="basket-summary">
          <div className="summary-row">
            <span>Sub Total:</span>
            <span>‚Çπ{subTotal.toFixed(2)}</span>
          </div>
          <div className="summary-row">
            <span>Discounts:</span>
            <span>-‚Çπ{discount.toFixed(2)}</span>
          </div>
          <div className="summary-row">
            <span>Delivery Fee:</span>
            <span>‚Çπ{deliveryFee.toFixed(2)}</span>
          </div>
        </div>
        <div className="basket-total">
          <span>Total to Pay:</span>
          <span>‚Çπ{total.toFixed(2)}</span>
        </div>
        <button className="checkouts-button" onClick={handleCheckout}>
          Choose Payment Method
        </button>
      </div>

      <Footer />
    </div>
  );
};

export default Baskets;
