import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Header from "../header/header";
import Footer from "../footer/Footer";
import "./Checkout.css";

const Checkout = () => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [totalItems, setTotalItems] = useState(0);
  const [salesTax] = useState(10); // Static tax value
  const location = useLocation();
  const basketItems = location.state?.basketItems || []; // Fallback to empty array if no data is passed

  const navigate = useNavigate();

  // Fetch cart items from backend API
  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const token = localStorage.getItem("token"); // Retrieve auth token
        const response = await fetch("/api/cart", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const data = await response.json();
        if (response.ok) {
          setCart(data.items);

          // Calculate total and totalItems for cart fetched from the API
          const totalPrice = data.items.reduce(
            (acc, item) => acc + item.price * item.quantity,
            0
          );
          const totalItemCount = data.items.reduce(
            (acc, item) => acc + item.quantity,
            0
          );

          setTotal(totalPrice);
          setTotalItems(totalItemCount);
        } else {
          console.error("Error fetching cart:", data.message);
        }
      } catch (error) {
        console.error("Error fetching cart:", error);
      }
    };

    fetchCartItems();
  }, []);

  // Combine basketItems (from location) with API cart items and calculate the total
  const combinedItems = [...basketItems, ...cart];

  // Calculate total price including sales tax
  const totalWithTax = total + salesTax;

  const handleProceedPayment = () => {
    navigate("/payment", { state: { totalWithTax } });
  };

  const handleDeliveryAddressClick = () => {
    navigate("/address");
  };

  // Calculate total for combined items (both from API and basketItems)
  const combinedTotal = combinedItems.reduce(
    (acc, item) => acc + item.price * item.quantity,
    0
  );
  const combinedTotalItems = combinedItems.reduce(
    (acc, item) => acc + item.quantity,
    0
  );

  return (
    <div>
      <Header />
      <div className="top">
        <img
          src="images/arrow-left.png"
          alt="arrow-left"
          onClick={() => navigate("/product")}
        />
        <h2>Your Order Details</h2>
      </div>
      <div className="checkout-container">
        <div className="bill">
          {/* Display Basket Items */}
          <div className="cart-items">
            {basketItems.length > 0 ? (
              basketItems.map((item) => (
                <div key={item.id}>
                  <p>{item.quantity}x {item.name}</p>
                  <p>Price: ₹{item.price}</p>
                  <p>Description: {item.description}</p>
                </div>
              ))
            ) : (
              <p>Your cart is empty.</p>
            )}
          </div>

          {/* Display Cart Items (from API) */}
          <div className="cart-items">
            {cart.length > 0 ? (
              cart.map((item) => (
                <div key={item.productId} className="cart-item">
                  <img
                    src={item.image}
                    alt={item.name}
                    className="cart-item-image"
                  />
                  <div className="cart-item-details">
                    <h3>{item.name}</h3>
                    <p>{item.quantity} x item</p>
                    <p>Price: ₹{item.price}</p>
                  </div>
                </div>
              ))
            ) : (
              <p>Your cart is empty.</p>
            )}
            <div className="notes">
              <label htmlFor="notes">Notes</label>
              <input id="notes" type="text" placeholder="Add order notes" />
            </div>
          </div>

          {/* Summary Section */}
          <div className="summary-section">
            <div
              className="delivery-address"
              onClick={handleDeliveryAddressClick}
            >
              <img src="images/delivery.png" alt="delivery location icon" />
              <div>
                <span>Delivery Address</span>
                <p>45, Green Street, Sector 12...</p>
              </div>
            </div>

            {/* Bill Summary */}
            <div className="bill-summary">
              <div className="checkout-summary">
                <p>Items</p>
                <span> ₹{combinedTotal}</span>
              </div>

              <div className="checkout-summary">
                <p>Sales Tax</p>
                <span>₹{salesTax}</span>
              </div>

              <div className="checkout-summary">
                <span>Subtotal ({combinedTotalItems} items)</span>
                <span>₹{combinedTotal + salesTax}</span>
              </div>
            </div>

            <button
              className="checkout-button"
              onClick={handleProceedPayment}
            >
              Choose Payment Method
            </button>
          </div>
        </div>
      </div>

      {/* Similar Restaurants Section */}
      <section className="restaurants">
        <div className="bc">
          <h8>Similar Restaurants</h8>
          <div className="restaurants-cards">
            <div className="Deals">
              <img src="/images/mc logo.png" alt="Burgers & Fast Food" />
            </div>
            <div className="Deals">
              <img src="/images/papa logo.png " alt="Salads" />
            </div>
            <div className="Deals">
              <img src="/images/KFC logo.png" alt="Pasta & Casuals" />
            </div>
            <div className="Deals">
              <img src="/images/texac logo.png" alt="Pizza" />
            </div>
            <div className="Deals">
              <img src="/images/burger king logo.png" alt="Breakfast" />
            </div>
            <div className="Deals">
              <img src="/images/shaurma logo.png" alt="Soups" />
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default Checkout;
