import React, { useEffect, useState } from "react";
import Header from "../header/header";
import Footer from "../footer/Footer";
import "./Product.css";

const Product = () => {



  if (!product) {
    return <p>Loading product details...</p>;
  }

  return (
    <div>
      <Header />

      {/* Main Banner */}
      <section className="banner">
        <div className="banner-content">
          <p>I'm lovin' it!</p>
          <h1>McDonald’s East London</h1>
          <div className="search-bar">
            <div id="input_container">
              <img src="Order Completed.png" id="myIcon" alt="order completed" />
              <input type="text" id="myInput" placeholder="Minimum Order: 12 GBP" />
            </div>
            <div id="input_container">
              <img src="Motocross.png" id="myIcons" alt="motocross" />
              <input type="text" id="myInputs" placeholder="Delivery in 20-25 Minutes" />
            </div>
          </div>

          <div className="home page">
            <img src="product rating.png" alt="background " />
          </div>
          <div className="home pageside image">
            <img src="product burgur.png" alt="home page side " />
          </div>
        </div>

        <div className="open">
          <img src="Clock.png" alt="clock 1" />
          <p>Open until 3:00 AM.</p>
        </div>
      </section>

      {/* Search Line */}
      <section className="search">
        <div className="extra">
          <h1>All Offers from McDonald's East London</h1>
          <div id="input_container">
            <img src="Search More.png" id="myIcons" alt="search" />
            <input type="text" id="myInputs" placeholder="Search for menu..." />
          </div>
        </div>
      </section>

      {/* Exclusive Deals */}
      <section className="exclusive-deals">
        <div className="just">
          <div className="bar">
            <ul className="bar-links">
              <li className="bar-item"><a href="#Vegan" className="bar-link">offers</a></li>
              <li className="bar-item"><a href="#Sushi" className="bar-link">burgers</a></li>
              <li className="bar-item"><a href="#fries" className="bar-link">fries</a></li>
              <li className="bar-item"><a href="#snacks" className="bar-link">snacks</a></li>
              <li className="bar-item"><a href="#fries" className="bar-link">Salads</a></li>
              <li className="bar-item"><a href="#fries" className="bar-link">cool drinks</a></li>
              <li className="bar-item"><a href="#fries" className="bar-link">Happy Meal® </a></li>
              <li className="bar-item"><a href="#fries" className="bar-link">Desserts</a></li>
              <li className="bar-item"><a href="#fries" className="bar-link">hot drinks</a></li>
              <li className="bar-item"><a href="#fries" className="bar-link">Souces</a></li>
              <li className="bar-item"><a href="#fries" className="bar-link">Orbit®</a></li>
            </ul>
          </div>
        </div>
      </section>

      {/* Deals */}
      <section>
        <div className="bn">
          <div className="deals-container">
            <div className="deal-card">
              <img src="1.png" alt="Chef Burgers London" />
            </div>
            <div className="deal-card">
              <img src="2.png" alt="Grand Ai Cafe London" />
            </div>
            <div className="deal-card">
              <img src="3.png" alt="Butterbrot Café London" />
            </div>
          </div>
        </div>
      </section>

      {/* Burgers Section */}
      <section className="burgers">
        <h7>Burgers</h7>
        <div className="category-cards">
          <div className="deals-card">
            <img src="combo 1.png" alt="Burgers & Fast Food" />
            <h2>Royal Cheese Burger with extra Fries</h2>
            <p>1 McChicken™, 1 Big Mac™,  1 Royal Cheeseburger, 3 medium</p>
            <h2>₹ 120</h2>
            <img src="plus background.png" alt="background" />
            <a href="destination URL">
              <img src="Plus.png" alt="plus button" />
            </a>
          </div>
          {/* Repeat similar deal cards */}
        </div>
      </section>

      {/* Fries Section */}
      <section className="Fries">
        <h7>Fries</h7>
        <div className="category-cards">
          {/* Repeat similar deal cards for fries */}
        </div>
      </section>

      {/* Cooldrinks Section */}
      <section className="cooldrinks">
        <h7>Cooldrinks</h7>
        <div className="category-cards">
          {/* Repeat similar deal cards for cool drinks */}
        </div>
      </section>

      {/* Customer Reviews */}
      <section className="customer review">
        <div className="head">
          <h1>Customer Reviews</h1>
          <img src="left.png" alt="left button" />
          <img src="right.png" alt="right button" />
          <div className="review">
            <img src="review.png" alt="review 1" />
            <img src="review.png" alt="review 2" />
            <img src="review.png" alt="review 3" />
          </div>
        </div>
      </section>

      {/* Similar Restaurants */}
      <section className="restaurants">
        <div className="bc">
          <h8>Similar Restaurants</h8>
          <div className="restaurants-cards">
            <div className="Deals">
              <img src="mc logo.png" alt="Burgers & Fast Food" />
            </div>
            {/* Repeat similar restaurant cards */}
          </div>
        </div>
      </section>



      <Footer />
    </div>
  );
};

export default Product;








































