import React, { useEffect, useState } from "react";
import api from "../../services/api";
import { useNavigate } from "react-router-dom";
import Header from "../header/header";
import './home.css'
const Home = () => {
    const [products, setProducts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await api.get("/products");
                setProducts(response.data.data);
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };

        fetchProducts();
    }, []);

    const addToCart = async (productId) => {
        try {
            await api.post("/cart", { productId, quantity: 1 });
            alert("Added to cart!");
        } catch (error) {
            alert(error.response?.data?.msg || "Failed to add to cart.");
        }
    };

    return (
        <div>
            <Header/>
            <section className="banner">
      <div className="banner-content">
        <p>Order Restaurant food, takeaway, and groceries.</p>

        <h1>Feast Your Senses,</h1>
        <h2>Fast and Fresh</h2>

        <p className="banner-subtext">Enter a postcode to see what we deliver</p>
        <div className="search-bar">
          <input type="text" placeholder="e.g., EC4R 3TE" />
          <button>Search</button>
        </div>
      </div>

      <div className="home-page">
        <img src="../images/home page_front.png" alt="Main Home Page" />
      </div>
      <div className="home-pageside-image">
        <img src="../images/home_page_side_image.png" alt="Side Home Page" />
      </div>
      <div className="home-page-background">
        <img src="../images/home_page_background.png" alt="Background" />
      </div>

      <div className="order order1">
        <img src="../images/order1.png" alt="Order 1" />
      </div>
      <div className="order order2">
        <img src="../images/order2.png" alt="Order 2" />
      </div>
      <div className="order order3">
        <img src="../images/order3.png" alt="Order 3" />
      </div>
    </section>
        </div>
    );
};

export default Home;
