import React, { useState } from "react";
import "./payment.css";
import Header from "../header/header";
import Footer from "../footer/Footer";

const PaymentPage = () => {
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState("Wallet");
  const [amount, setAmount] = useState(240); // Default amount
  const walletBalance = 390;

  const paymentMethods = [
    { id: "Wallet", name: "Wallet", icon: "/images/wallet.png" },
    { id: "MaestroCard", name: "MaestroCard", icon: "/images/maestro-card.png" },
    { id: "PayPal", name: "PayPal", icon: "/images/paypal.png" },
    { id: "Stripe", name: "Stripe", icon: "/images/stripe.png" },
  ];

  const handlePaymentSelection = (method) => {
    setSelectedPaymentMethod(method);
  };

  const handleProceedPayment = () => {
    alert(`Proceeding with ${selectedPaymentMethod} for ₹${amount}`);
  };

  return (
    <div className="payment-page">
      <Header />
      <section className="payment-section">
        <div className="payment-header">
          <button className="back-button">&larr;</button>
          <h1>Payment Gateway</h1>
        </div>

        <div className="payment-body">
          {/* Payment Methods */}
          <div className="payment-methods">
            {paymentMethods.map((method) => (
              <div
                key={method.id}
                className={`payment-method ${
                  selectedPaymentMethod === method.id ? "selected" : ""
                }`}
                onClick={() => handlePaymentSelection(method.id)}
              >
                <img src={method.icon} alt={method.name} />
                <span>{method.name}</span>
              </div>
            ))}

            <div className="payment-method add-card">
              <span>+ Add New Card</span>
            </div>
          </div>

          {/* Payment Summary */}
          <div className="payment-summary">
            <h3>Amount to be Paid</h3>
            <div className="amount-box">₹{amount}</div>
            <button
              className="proceed-button"
              onClick={handleProceedPayment}
              disabled={
                selectedPaymentMethod === "Wallet" && amount > walletBalance
              }
            >
              Proceed to Pay
            </button>
          </div>
        </div>
      </section>
      <Footer />
    </div>
  );
};

export default PaymentPage;
