import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import Header from "../header/header";
import Footer from "../footer/Footer";
import "./Checkout.css";

const Checkout = () => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [salesTax] = useState(10);

  useEffect(() => {
    const fetchCartItems = () => {
      const mockCart = [
        { id: 1, name: "Big Mac", image: "/images/bigmac.jpg", price: 120, quantity: 2 },
        { id: 2, name: "French Fries", image: "/images/fries.jpg", price: 50, quantity: 1 },
        { id: 3, name: "Chicken McNuggets", image: "/images/nuggets.jpg", price: 100, quantity: 1 },
      ];
      setCart(mockCart);
      const totalPrice = mockCart.reduce(
        (acc, item) => acc + item.price * item.quantity,
        0
      );
      setTotal(totalPrice);
    };

    fetchCartItems();
  }, []);

  const totalWithTax = total + salesTax;

  return (
    <div>
      <Header />
      <div className="checkout-container">
        <h2>Your Order Details</h2>
        <div className="cart-items">
          {cart.map((item) => (
            <div key={item.id} className="cart-item">
              <img src={item.image} alt={item.name} />
              <div className="cart-item-details">
                <h3>{item.name}</h3>
                <p>Price: ₹{item.price}</p>
                <p>Quantity: {item.quantity}</p>
              </div>
            </div>
          ))}
        </div>

        <div className="delivery-address">
          <img src="images/delivery.png" alt="delivery location icon" />
          <div>
            <span>Delivery Address</span>
            <p>45, Green Street, Sector 12...</p>
          </div>
        </div>

        <div className="bill-summary">
          <div className="checkout-summary">
            <span>Items</span>
            <span>₹{total}</span>
          </div>
          <p>
            <span>Sales Tax</span> <span>₹{salesTax}</span>
          </p>
          <div className="subtotal">
            <h3>Total (including tax) ₹{totalWithTax}</h3>
          </div>
        </div>

        <Link to="/payment">
          <button className="checkout-button">Choose Payment Method</button>
        </Link>
      </div>

      <Footer />
    </div>
  );
};

export default Checkout;






































