import React, { useState } from "react";
import Header from "../header/header";
import Footer from "../footer/Footer";
import { useNavigate } from "react-router-dom";
import "./basket.css";

const Baskets = () => {
  const [basketItems, setBasketItems] = useState([
    {
      id: 1,
      name: "Royal Cheese Burger",
      description: "With extra fries",
      price: 120,
      quantity: 1,
      image: "/images/combo 1.png",
    },
    {
      id: 2,
      name: "Potato Veggies",
      description: "No Mushrooms + green peppers",
      price: 70,
      quantity: 1,
      image: "/images/fries1.png",
    },
    {
      id: 3,
      name: "Coke Coca Cola",
      description: "",
      price: 40,
      quantity: 1,
      image: "/images/cooldrink1.png",
    },
  ]);

  const handleAddToCart = (newItem) => {
    const itemIndex = basketItems.findIndex((item) => item.id === newItem.id);

    if (itemIndex >= 0) {
      const updatedItems = [...basketItems];
      updatedItems[itemIndex].quantity += 1;
      setBasketItems(updatedItems);
    } else {
      setBasketItems([...basketItems, { ...newItem, quantity: 1 }]);
    }
  };

  const subTotal = basketItems.reduce((total, item) => total + item.price * item.quantity, 0);
  const discount = 3;
  const deliveryFee = 3;
  const total = subTotal - discount + deliveryFee;

  const navigate = useNavigate();

  const handleCheckout = () => {
    navigate("/checkout");
  };

  return (
    <div>
      <Header />

      <section>
        <h3>Burgers</h3>
        <div className="products">
          <div className="product-card">
            <div className="content">
              <h2>Royal Cheese Burger</h2>
              <p>With extra fries</p>
              <h2>₹ 120</h2>
            </div>
            <div className="images">
              <img src="/images/combo 1.png" alt="Royal Cheese Burger" />
              <img src="/images/plus background.png" alt="background" />
              <button
                onClick={() =>
                  handleAddToCart({
                    id: 1,
                    name: "Royal Cheese Burger",
                    price: 120,
                    description: "With extra fries",
                    image: "/images/combo 1.png",
                  })
                }
              >
                <img src="/images/Plus.png" alt="plus button" />
              </button>
            </div>
          </div>

          <div className="product-card">
            <div className="content">
              <h2>Potato Veggies</h2>
              <p>No Mushrooms + green peppers</p>
              <h2>₹ 70</h2>
            </div>
            <div className="images">
              <img src="/images/fries1.png" alt="Potato Veggies" />
              <img src="/images/plus background.png" alt="background" />
              <button
                onClick={() =>
                  handleAddToCart({
                    id: 2,
                    name: "Potato Veggies",
                    price: 70,
                    description: "No Mushrooms + green peppers",
                    image: "/images/fries1.png",
                  })
                }
              >
                <img src="/images/Plus.png" alt="plus button" />
              </button>
            </div>
          </div>
        </div>
      </section>

      <section>
        <h3>Cooldrinks</h3>
        <div className="products">
          <div className="product-card">
            <div className="content">
              <h2>Coke Coca Cola</h2>
              <h2>₹ 40</h2>
            </div>
            <div className="images">
              <img src="/images/cooldrink1.png" alt="Coke Coca Cola" />
              <img src="/images/plus background.png" alt="background" />
              <button
                onClick={() =>
                  handleAddToCart({
                    id: 3,
                    name: "Coke Coca Cola",
                    price: 40,
                    description: "",
                    image: "/images/cooldrink1.png",
                  })
                }
              >
                <img src="/images/Plus.png" alt="plus button" />
              </button>
            </div>
          </div>
        </div>
      </section>

      <div className="basket-container">
        <h3>Your Basket</h3>
        <div className="basket-items">
          {basketItems.map((item) => (
            <div key={item.id} className="basket-item">
              <img src={item.image} alt={item.name} />
              <div>
                <h4>{item.name}</h4>
                <p>{item.description}</p>
                <p>Price: ₹ {item.price}</p>
                <p>Quantity: {item.quantity}</p>
              </div>
            </div>
          ))}
        </div>

        <div className="basket-summary">
          <p>Subtotal: ₹ {subTotal}</p>
          <p>Discount: ₹ {discount}</p>
          <p>Delivery Fee: ₹ {deliveryFee}</p>
          <h2>Total: ₹ {total}</h2>
        </div>

        <button className="checkouts-button" onClick={handleCheckout}>
          Choose Payment Method
        </button>
      </div>

      <Footer />
    </div>
  );
};

export default Baskets;
