import React, { useState } from "react";
import "./payment.css";
import Header from "../header/header";
import Footer from "../footer/Footer";

const PaymentPage = () => {
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState("Wallet");
  const [amount, setAmount] = useState(240); // Default amount
  const walletBalance = 390;

  const paymentMethods = [
    { id: "Wallet", name: "Wallet", icon: "/images/wallet.png" },
    { id: "Maestrokard", name: "Maestrokard", icon: "/images/m.png" },
    { id: "PayPal", name: "PayPal", icon: "/images/p.png" },
    { id: "Stripe", name: "Stripe", icon: "/images/s.png" },
  ];

  const handlePaymentSelection = (method) => {
    setSelectedPaymentMethod(method);
  };

  const handleProceedPayment = () => {
    alert(`Proceeding with ${selectedPaymentMethod} for ₹${amount}`);
  };

  return (
    <div className="payment-page">
      <Header />
      <section className="payment">
      {/* Back Arrow and Title */}
      <div className="payment-header">
        <img src="/images/arrow-left.png" alt="Back" className="back-arrow" />
        <h2>Choose and Pay</h2>
      </div>

      <div className="payment-layout">
        {/* Payment Options */}
        <div className="payment-container">
          {/* Wallet Option */}
          <div
            className={`payment-option wallet ${
              selectedPaymentMethod === "wallet" ? "selected" : ""
            }`}
            onClick={() => handlePaymentSelection("wallet")}
          >
            <div className="wallet-info">
              <p>Wallet</p>
              <span>Available balance: ₹{walletBalance}</span>
            </div>
            <input
              type="radio"
              name="payment-method"
              value="wallet"
              checked={selectedPaymentMethod === "wallet"}
              readOnly
            />
          </div>

          {/* Other Payment Methods */}
          <div
            className={`payment-option ${
              selectedPaymentMethod === "maestrocard" ? "selected" : ""
            }`}
            onClick={() => handlePaymentSelection("maestrocard")}
          >
            <div className="payment-icon">M</div>
            <p>MaestroCard</p>
            <input
              type="radio"
              name="payment-method"
              value="maestrocard"
              checked={selectedPaymentMethod === "maestrocard"}
              readOnly
            />
          </div>
          <div
            className={`payment-option ${
              selectedPaymentMethod === "paypal" ? "selected" : ""
            }`}
            onClick={() => handlePaymentSelection("paypal")}
          >
            <div className="payment-icon">P</div>
            <p>PayPal</p>
            <input
              type="radio"
              name="payment-method"
              value="paypal"
              checked={selectedPaymentMethod === "paypal"}
              readOnly
            />
          </div>
          <div
            className={`payment-option ${
              selectedPaymentMethod === "stripe" ? "selected" : ""
            }`}
            onClick={() => handlePaymentSelection("stripe")}
          >
            <div className="payment-icon">S</div>
            <p>Stripe</p>
            <input
              type="radio"
              name="payment-method"
              value="stripe"
              checked={selectedPaymentMethod === "stripe"}
              readOnly
            />
          </div>
          <div className="payment-option add-card">
            <div className="payment-icon">+</div>
            <p>Add Debit Card</p>
          </div>
        </div>

        {/* Payment Summary */}
        <div className="payment-summary">
          <div className="amount-section">
            <p className="amount-label">Amount to be paid:</p>
            <p className="amount-value">₹{amount}</p>
          </div>
          <button
            className="proceed-payment"
            onClick={handleProceedPayment}
            disabled={
              selectedPaymentMethod === "wallet" && amount > walletBalance
            }
          >
            Proceed Payment
          </button>
        </div>
      </div>
    </section>
      <Footer />
    </div>
  );
};

export default PaymentPage;
