import React, { useState } from "react";
import Header from "../header/header";
import Footer from "../footer/Footer";
import "./Product.css";
import Basket from "../myBasket/basket";

const Product = () => {
  const [basketItems, setBasketItems] = useState([
    {
      id: 1,
      name: "Royal Cheese Burger",
      description: "With extra fries",
      price: 120,
      quantity: 1,
    },
    {
      id: 2,
      name: "Potato Veggies",
      description: "No Mushrooms + green peppers",
      price: 70,
      quantity: 1,
    },
    {
      id: 3,
      name: "Coke Coca Cola",
      description: "",
      price: 40,
      quantity: 1,
    },
  ]);

  const handleCopyLink = () => {
    navigator.clipboard.writeText("Your link here!");
    alert("Link copied to clipboard!");
  };

  const handleAddToCart = (newItem) => {
    const itemIndex = basketItems.findIndex((item) => item.id === newItem.id);

    if (itemIndex >= 0) {
      const updatedItems = [...basketItems];
      updatedItems[itemIndex].quantity += 1;
      setBasketItems(updatedItems);
    } else {
      setBasketItems([...basketItems, { ...newItem, quantity: 1 }]);
    }
  };

  const renderProducts = (category, products) => (
    <section className={category}>
      <h3>{category}</h3>
      <div className="category-cards">
        {products.map((product) => (
          <div className="product-card" key={product.id}>
            <div className="content">
              <h2>{product.name}</h2>
              <p>{product.description}</p>
              <h2>₹ {product.price}</h2>
            </div>
            <div className="images">
              <img src={product.image} alt={product.name} />
              <img src="/images/plus background.png" alt="background" />
              <button onClick={() => handleAddToCart(product)}>
                <img src="/images/Plus.png" alt="Add to cart" />
              </button>
            </div>
          </div>
        ))}
      </div>
    </section>
  );

  const productsData = {
    Burgers: [
      {
        id: 1,
        name: "Royal Cheese Burger",
        description: "With extra fries",
        price: 120,
        image: "/images/combo 1.png",
      },
      {
        id: 2,
        name: "The classics for 2",
        description: "1 McChicken™, 1 Big Mac™, 3 Fries, 3 Drinks",
        price: 120,
        image: "/images/combo 2.png",
      },
      // Add more burgers as needed
    ],
    Fries: [
      {
        id: 3,
        name: "Large Fries",
        description: "Crispy golden fries",
        price: 70,
        image: "/images/fries1.png",
      },
      {
        id: 4,
        name: "The classics for 2",
        description: "1 McChicken™, 3 Fries, 3 Drinks",
        price: 70,
        image: "/images/fries2.png",
      },
      // Add more fries as needed
    ],
    Cooldrinks: [
      {
        id: 5,
        name: "Coke Coca Cola",
        description: "",
        price: 40,
        image: "/images/cooldrink1.png",
      },
      {
        id: 6,
        name: "Sprite",
        description: "",
        price: 40,
        image: "/images/cooldrink2.png",
      },
      // Add more cool drinks as needed
    ],
  };

  return (
    <div>
      <Header />
      {/* Banner Section */}
      <section className="banner1">
        <div className="banner-content1">
          <p>I'm lovin' it!</p>
          <h1>McDonald’s East London</h1>
          <div className="search-bar1">
            <div id="input_container">
              <img src="/images/Order Completed.png" alt="order completed" />
              <div>Minimum Order: 12 GBP</div>
            </div>
            <div id="input_container">
              <img src="/images/Motocross.png" alt="motocross" />
              <div>Delivery in 20-25 Minutes</div>
            </div>
          </div>
        </div>
      </section>
      {/* Render Products */}
      {Object.entries(productsData).map(([category, products]) =>
        renderProducts(category, products)
      )}
      {/* Share and Basket Section */}
      <div className="share-cart-container">
        <img src="/images/share-2.png" alt="share" />
        <span>Share this cart with your friends</span>
        <button onClick={handleCopyLink}>Copy Link</button>
      </div>
      <Basket items={basketItems} />
      <Footer />
    </div>
  );
};

export default Product;
