// import React, { useEffect, useState } from "react";
import api from "../../services/api";
import { useNavigate } from "react-router-dom";
import Header from "../header/header";
import Footer from "../footer/Footer";
import './home.css';
import { useState } from "react";
const Home = () => {
    const [products, setProducts] = useState([]);
    const navigate = useNavigate();

//  check below const
    const handleCardClick = (productId) => {
      navigate(`/product`); // Redirect to Product page
    };

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await api.get("/product");
                setProducts(response.data.data);  
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };

        fetchProducts();
    }, []);

    const addToCart = async (productId) => {
        try {
            await api.post("/cart", { productId, quantity: 1 });
            alert("Added to cart!");
        } catch (error) {
            alert(error.response?.data?.msg || "Failed to add to cart.");
        }
    };





    // check the below div
    <div className="deals-container">
  {products.map((product) => (
    <div
      className="deal-card"
      key={product.id}
      onClick={() => handleCardClick(product.id)}
    >
      <img src={product.image} alt={product.name} />
      <h2>{product.name}</h2>
    </div>
  ))}
</div>

    return (
        <div>
          <Header /> {/* Header Component */}

     {/* Main Banner */}
     <section className="banner">
  <div className="banner-content">
    <p>Order Restaurant food, takeaway, and groceries.</p>

    {/* Main Headings */}
    <h1>Feast Your Senses,</h1>
    <h2>Fast and Fresh</h2>

    {/* Postcode Input */}
    <p>Enter a postcode to see what we deliver</p>
    <div className="search-bar">
      <input type="text" placeholder="e.g., EC4R 3TE" />
      <button>Search</button>
    </div>

    {/* Images */}
    <div className="home-page">
      <img src="/images/home page  front.png" alt="Home Page Main" />
    </div>
    <div className="home-page-side-image">
      <img src="/images/hiome page front side .png" alt="Home Page Side " />
    </div>
    <div className="home-page-background">
      <img src="/images/home page background.png" alt="Home Page Background" />
    </div>

    {/* Notifications */}
    <div className="order order1">
      <img src="/images/order 3.png" alt="Order 1" />
    </div>
    <div className="order order2">
      <img src="/images/order 3.png" alt="Order 2" />
    </div>
    <div className="order order3">
      <img src="/images/order 3.png" alt="Order 3" />
    </div>
  </div>
</section>









          {/* Exclusive Deals */}
          <section className="exclusive-deals">
            <div className="bar">
              <h8>Up to -40% 🎊 Order.uk exclusive deals</h8>
              <ul className="bar-links">
                <li><a href="#Vegan">Vegan</a></li>
                <li><a href="#Sushi">Sushi</a></li>
                <li><a href="#PizzaFastFood">Pizza & Fast food</a></li>
                <li><a href="#others">Others</a></li>
              </ul>
            </div>
            <div className="deals-container">
              <div className="deal-card">
                <img src="/images/deals 1.png" alt="Chef Burgers London" />
              </div>
              <div className="deal-card">
                <img src="/images/deal 2.png" alt="Grand Ai Cafe London" />
              </div>
              <div className="deal-card">
                <img src="../images/deal 3.png" alt="Butterbrot Café London" />
              </div>
            </div>
          </section>


  {/*  Popular Categories  */}

<section class="categories">
   <div class="bj">
  <h8>Order.uk Popular Categories 🤩</h8>
  <div class="category-cards">
      <div class="deals-card">
          <img src="/images/categories 1.png" alt="Burgers & Fast Food"/>
          <h2>Burgers & Fast Food</h2>
          <p>21 Restaurants</p>
      </div>
      <div class="deals-card">
          <img src="/images/categories 2.png" alt="Salads"/>
          <h2>Salads</h2>
          <p>32 Restaurants</p>
      </div>
      <div class="deals-card">
          <img src="/images/categories 3.png" alt="Pasta & Casuals"/>
          <h2>Pasta & Casuals</h2>
          <p>4 Restaurants</p>
      </div>
      <div class="deals-card">
          <img src="/images/categories 4.png" alt="Pizza"/>
          <h2>Pizza</h2>
          <p>32 Restaurants</p>
      </div>
      <div class="deals-card">
          <img src="/images/categories 5.png" alt="Breakfast"/>
          <h2>Breakfast</h2>
          <p>4 Restaurants</p>
      </div>
      <div class="deals-card">
          <img src="/images/categories 6.png" alt="Soups"/>
          <h2>Soups</h2>
          <p>32 Restaurants</p>
      </div>
  </div>
</div>
</section>


{/* Popular Restaurants  */}
 <section class="restaurants">
  <div class="bc">
 <h8>Popular Restaurants</h8>
 <div class="restaurants-cards">
     <div class="deals-card">
         <img src="/images/mc logo.png" alt="Burgers & Fast Food"/>
     </div>
     <div class="deals-card">
         <img src="/images/papa logo.png " alt="Salads"/>
     </div>
     <div class="deals-card">
         <img src="/images/KFC logo.png" alt="Pasta & Casuals"/>
     </div>
     <div class="deals-card">
         <img src="/images/texac logo.png" alt="Pizza"/>
     </div>
     <div class="deals-card">
         <img src="/images/burger king logo.png" alt="Breakfast"/>
     </div>
     <div class="deals-card">
         <img src="/images/shaurma logo.png" alt="Soups"/>
     </div>
 </div>
</div>
</section>


{/* Partner Program */}
<section class="partner-program">
  <div class="ordering image">
      <img src="/images/Ordering APP.png" alt="Ordering app"/>
  </div>
</section>

<section class="partner-program">
  <div class="partner-cards">
      <img src="/images/partner with us.png" alt="partner"/>
  </div>
  <div class="ride">
      <img src="/images/ride with us.png" alt="ride"/>
  </div>
</section>



{/*  FAQs  */}
<section class="faq">
    <div class="bl">
        <div class="faq-cards">
            <h6>Know more about us!</h6>
            <ul>
                <li><a href="#ques">Frequent Questions</a></li>
                <li><a href="#who">Who we are?</a></li>
                <li><a href="#partner">Partner Program</a></li>
                <li><a href="#support">Help & Support</a></li>
            </ul>
        </div>

        <section class="FAQS">
            <div class="faqs-cards">
                <ul>
                    <li><a href="#work">How does Order.UK work?</a></li>
                    <li><a href="#accept">What payment methods are accepted?</a></li>
                    <li><a href="#time">Can I track my order in real-time?</a></li>
                    <li><a href="#discount">Are there any special discounts or promotions available?</a></li>
                    <li><a href="#area">Is Order.UK available in my area?</a></li>
                </ul>
            </div>

            <div class="faqs-cards-img">
                <div class="faqs-cards-img-1">
                    <h7>Place an order!</h7>
                    <img src="images/order-food 1.png" alt="Place an order through our website or mobile app"/>
                    <p>Place your order through our website or mobile app</p>
                </div>
                <div class="faqs-cards-img-1">
                    <h7>Track Progress</h7>
                    <img src="/images/food 2.png" alt="Track the status of your order"/>
                    <p>You can track your order status with the delivery time</p>
                </div>
                <div class="faqs-cards-img-1">
                    <h7>Get your Order!</h7>
                    <img src="/images/order 2.png" alt="Receive your order at lightning-fast speed"/>
                    <p>Receive your order at lightning-fast speed!</p>
                </div>
            </div>

            <p>Order.UK simplifies the food ordering process. Browse through our diverse menu, select your favorite dishes, and proceed to checkout. Your delicious meal will be on its way to your doorstep in no time!</p>
        </section>
    </div>
</section>





          <Footer />                   
        </div>
      );

};

export default Home;






















