import React, { useState } from "react";
import Header from "../header/header";
import Footer from "../footer/Footer";
import "./Product.css";
import Basket from "../myBasket/basket";

const Product = () => {
  const [basketItems, setBasketItems] = useState([]);

  const handleAddToBasket = (product) => {
    setBasketItems((prevItems) => {
      const existingItem = prevItems.find((item) => item.id === product.id);
      if (existingItem) {
        return prevItems.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        return [...prevItems, { ...product, quantity: 1 }];
      }
    });
  };

  const handleRemoveFromBasket = (id) => {
    setBasketItems((prevItems) =>
      prevItems.filter((item) => item.id !== id)
    );
  };

  const products = [
    { id: 1, name: "Royal Cheese Burger", description: "With extra fries", price: 120 },
    { id: 2, name: "Potato Veggies", description: "No Mushrooms + green peppers", price: 70 },
    { id: 3, name: "Coke Coca Cola", description: "", price: 40 },
  ];

  return (
    <div>
      <Header />
      <section className="product-section">
        <h3>Products</h3>
        <div className="category-cards">
          {products.map((product) => (
            <div className="product-card" key={product.id}>
              <div className="content">
                <h2>{product.name}</h2>
                <p>{product.description}</p>
                <h2>â‚¹{product.price}</h2>
              </div>
              <div className="images">
                <img src="/images/product-image.png" alt={product.name} />
                <button
                  onClick={() => handleAddToBasket(product)}
                  className="add-to-basket-button"
                >
                  <img src="/images/Plus.png" alt="Add to Basket" />
                </button>
              </div>
            </div>
          ))}
        </div>
      </section>
      <Basket items={basketItems} handleRemove={handleRemoveFromBasket} />
    </div>
  );
};

export default Product;
