import React, { useState } from "react";

const App = () => {
  // State to hold basket items and added products
  const [basketItems, setBasketItems] = useState([]);
  const [addedProducts, setAddedProducts] = useState(new Set());

  // Handle adding products to the basket
  const handleAddToCart = (newItem) => {
    const itemIndex = basketItems.findIndex((item) => item.id === newItem.id);

    if (itemIndex >= 0) {
      const updatedItems = [...basketItems];
      updatedItems[itemIndex].quantity += 1;
      setBasketItems(updatedItems);
    } else {
      setBasketItems([...basketItems, { ...newItem, quantity: 1 }]);
    }

    // Add the product ID to the `addedProducts` state
    setAddedProducts((prev) => new Set(prev).add(newItem.id));

    // Optional: Remove the "Added" state after a timeout
    setTimeout(() => {
      setAddedProducts((prev) => {
        const updatedSet = new Set(prev);
        updatedSet.delete(newItem.id);
        return updatedSet;
      });
    }, 3000); // Reset after 3 seconds
  };

  // Sample product list
  const products = [
    { id: 1, name: "Cheese Burger", price: 150 },
    { id: 2, name: "Veggie Pizza", price: 200 },
    { id: 3, name: "Grilled Sandwich", price: 120 },
  ];

  return (
    <div style={{ padding: "20px" }}>
      <h1>Shopping Cart</h1>

      {/* Product List */}
      <div style={{ display: "flex", gap: "20px" }}>
        {products.map((product) => (
          <div
            key={product.id}
            style={{
              border: "1px solid #ccc",
              padding: "10px",
              borderRadius: "5px",
              textAlign: "center",
            }}
          >
            <h3>{product.name}</h3>
            <p>Price: ₹{product.price}</p>
            <button
              onClick={() => handleAddToCart(product)}
              disabled={addedProducts.has(product.id)}
              style={{
                padding: "10px",
                backgroundColor: addedProducts.has(product.id)
                  ? "green"
                  : "blue",
                color: "white",
                border: "none",
                cursor: addedProducts.has(product.id) ? "not-allowed" : "pointer",
              }}
            >
              {addedProducts.has(product.id) ? "Added to Basket" : "Add to Basket"}
            </button>
          </div>
        ))}
      </div>

      {/* Basket Summary */}
      <div style={{ marginTop: "20px" }}>
        <h2>Basket</h2>
        {basketItems.length === 0 ? (
          <p>No items in the basket</p>
        ) : (
          <ul>
            {basketItems.map((item) => (
              <li key={item.id}>
                {item.name} - Quantity: {item.quantity} - Price: ₹
                {item.price * item.quantity}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default App;
