import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Header from "../header/header";
import Footer from "../footer/Footer";
import "./Product.css";

const Product = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]); // State to hold cart items
  const [isCartVisible, setCartVisible] = useState(false); // State to toggle cart visibility

  useEffect(() => {
    // Simulate fetching product details
    const fetchProduct = async () => {
      const mockProducts = {
        1: { id: 1, name: "Big Mac", description: "Tasty burger", price: 120 },
        2: { id: 2, name: "Chicken McNuggets", description: "Crispy nuggets", price: 100 },
        3: { id: 3, name: "French Fries", description: "Crunchy fries", price: 50 },
      };

      setTimeout(() => {
        setProduct(mockProducts[productId]);
        setLoading(false);
      }, 500); // Simulate API delay
    };

    fetchProduct();
  }, [productId]);

  // Add item to cart
  const addToCart = (item) => {
    setCart([...cart, item]);
    setCartVisible(true); // Show the cart when an item is added
  };

  // Remove item from cart
  const removeFromCart = (indexToRemove) => {
    setCart(cart.filter((_, index) => index !== indexToRemove));
  };

  // Calculate total price
  const getTotal = () => {
    return cart.reduce((acc, item) => acc + item.price, 0);
  };

  if (loading) return <p>Loading...</p>;
  if (!product) return <p>Product not found.</p>;

  return (
    <div>
      <Header />
      {/* Product Details */}
      <section className="product-details">
        <h1>{product.name}</h1>
        <p>{product.description}</p>
        <p>Price: ₹ {product.price}</p>
        <button onClick={() => addToCart(product)}>Add to Cart</button>
      </section>

      {/* Cart Section */}
      <div className={`cart-container ${isCartVisible ? "visible" : "hidden"}`}>
        <h3>Your Cart</h3>
        <ul>
          {cart.map((item, index) => (
            <li key={index}>
              {item.name} - ₹ {item.price}{" "}
              <button onClick={() => removeFromCart(index)}>Remove</button>
            </li>
          ))}
        </ul>
        <p>
          <strong>Total:</strong> ₹ {getTotal()}
        </p>
        <button onClick={() => alert("Proceeding to checkout!")}>
          Checkout
        </button>
      </div>

      <Footer />
    </div>
  );
};

export default Product;
