import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../header/header";
import Footer from "../footer/Footer";
import "./Checkout.css";  

const Checkout = () => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [salesTax, setSalesTax] = useState(0);
  const [notes, setNotes] = useState("");  // for capturing notes
  const [address, setAddress] = useState("45, Green Street, Sector 12...");  // for dynamic delivery address

  useEffect(() => {
    // Simulate fetching cart items
    const fetchCartItems = () => {
      const mockCart = [
        {
          id: 1,  
          name: "Big Mac",
          image: "/images/combo 4.png",
          price: 120,
          quantity: 2,
        },
        {
          id: 2,
          name: "French Fries",
          image: "/images/fries1.png",
          price: 50,
          quantity: 1,
        },
        {
          id: 3,
          name: "Cooldrinks",
          image: "/images/cooldrink1.png",
          price: 100,
          quantity: 1,
        },
      ];
      setCart(mockCart);

      // Calculate total price
      const totalPrice = mockCart.reduce(
        (acc, item) => acc + item.price * item.quantity,
        0
      );
      setTotal(totalPrice);

      // Calculate sales tax (10% of total)
      const calculatedSalesTax = totalPrice * 0.10;
      setSalesTax(calculatedSalesTax);
    };

    fetchCartItems();
  }, []);

  // Calculate total price including sales tax
  const totalWithTax = total + salesTax;

  const navigate = useNavigate();

  const handleProceedPayment = () => {
    navigate('/payment', { state: { totalWithTax } });
  };

  const handleDeliveryAddressClick = () => {
    navigate('/address');
  };

  const handleNotesChange = (e) => {
    setNotes(e.target.value); // Update notes state
  };

  return (
    <div>
      <Header />
      <div className="top">
        <img
          src="images/arrow-left.png"
          alt="arrow-left"
          onClick={() => navigate("/product")}
        />
        <h2>Your Order Details</h2>
      </div>
      <div className="checkout-container">
        <div className="bill">
          <div className="cart-items">
            {cart.map((item) => (
              <div key={item.id} className="cart-item">
                <img
                  src={item.image}
                  alt={`Image of ${item.name}`} // Improved alt text for accessibility
                  className="cart-item-image"
                />
                <div className="cart-item-details">
                  <h3>{item.name}</h3>
                  <p>{item.quantity} x item</p>
                  <p>Price: ₹{item.price}</p>
                </div>
              </div>
            ))}
            <div className="notes">
              <label htmlFor="notes">Notes</label>
              <input
                id="notes"
                type="text"
                placeholder="Add order notes"
                value={notes}
                onChange={handleNotesChange} // Capture notes input
              />
            </div>
          </div>

          <div className="summary-section">
            <div
              className="delivery-address"
              onClick={handleDeliveryAddressClick}
            >
              <img src="images/delivery.png" alt="delivery location icon" />
              <div>
                <span>Delivery Address</span>
                <p>{address}</p> {/* Display dynamic address */}
              </div>
            </div>

            <div className="bill-summary">
              <div className="checkout-summary">
                <p>Items</p>
                <span> ₹{total}</span>
              </div>

              <div className="checkout-summary">
                <p>Sales Tax</p>
                <span>₹{salesTax}</span>
              </div>

              <div className="checkout-summary">
                <span>Subtotal({cart.length} items)</span> {/* Dynamically count items */}
                <span>₹{totalWithTax}</span>
              </div>
            </div>

            <button
              className="checkout-button"
              onClick={handleProceedPayment}
            >
              Choose Payment Method
            </button>
          </div>
        </div>
      </div>

      {/* Similar Restaurants */}
      <section className="restaurants">
        <div className="bc">
          <h8>Similar Restaurants</h8>
          <div className="restaurants-cards">
            <div className="Deals">
              <img
                src="/images/mc logo.png"
                alt="Burgers & Fast Food"
              />
            </div>
            <div className="Deals">
              <img src="/images/papa logo.png" alt="Salads" />
            </div>
            <div className="Deals">
              <img src="/images/KFC logo.png" alt="Pasta & Casuals" />
            </div>
            <div className="Deals">
              <img src="/images/texac logo.png" alt="Pizza" />
            </div>
            <div className="Deals">
              <img src="/images/burger king logo.png" alt="Breakfast" />
            </div>
            <div className="Deals">
              <img src="/images/shaurma logo.png" alt="Soups" />
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default Checkout;


































