const express = require("express");
const jwt = require("jsonwebtoken");
const bcrypt = require("bcryptjs");
const router = express.Router();

// Example in-memory user data (you would typically get this from a database)
const users = [
    { email: "test@example.com", password: "$2a$10$gqlMOFfcHhpi1OAkGkHsMeq0VXErbZ79clC.4kPoXk8tqhtTdl8Jq" } // Hashed password
];

// Login route
router.post("/login", async (req, res) => {
    const { email, password } = req.body;

    // Find the user by email
    const user = users.find((user) => user.email === email);
    if (!user) {
        return res.status(400).json({ message: "Invalid email or password." });
    }

    // Compare password with the stored hash
    const isMatch = await bcrypt.compare(password, user.password);
    if (!isMatch) {
        return res.status(400).json({ message: "Invalid email or password." });
    }

    // Generate a JWT token (replace 'secret' with an actual secret key)
    const token = jwt.sign({ email: user.email }, 'secret', { expiresIn: '1h' });

    // Send the token in the response
    res.json({ token });
});

module.exports = router;
