import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../../services/api";


const Product = () => {
  const { branchId } = useParams();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBranchProducts = async () => {
      try {
        const response = await api.get(`/branch/${branchId}/products`);
        setProducts(response.data);
      } catch (error) {
        console.error("Error fetching products:", error);
        alert("Failed to load products for this branch.");
      } finally {
        setLoading(false);
      }
    };

    fetchBranchProducts();
  }, [branchId]);

  if (loading) return <p>Loading...</p>;

  if (!products || products.length === 0) {
    return <p>No products available for this branch.</p>;
  }

  return (
    <div>
      <h1>Products for Branch {branchId}</h1>
      <div>
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <h2>{product.name}</h2>
            <p>{product.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Product;
