import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import Header from "../header/header";
import Footer from "../footer/Footer";
// import "./checkout.css";  
import "./checkout.css";  
<div style={{ backgroundColor: "yellow" }}>Test Element</div>

const Checkout = () => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [salesTax] = useState(10); 

  useEffect(() => {
    // Simulate fetching cart items
    const fetchCartItems = () => {
      const mockCart = [
        {
          id: 1,
          name: "Big Mac",
          image: "/images/bigmac.jpg",
          price: 120,
          quantity: 2,
        },
        {
          id: 2,
          name: "French Fries",
          image: "/images/fries.jpg",
          price: 50,
          quantity: 1,
        },
        {
          id: 3,
          name: "Chicken McNuggets",
          image: "/images/nuggets.jpg",
          price: 100,
          quantity: 1,
        },
      ];
      setCart(mockCart);
      const totalPrice = mockCart.reduce(
        (acc, item) => acc + item.price * item.quantity,
        0
      );
      setTotal(totalPrice);
    };

    fetchCartItems();
  }, []);

  // Calculate total price including sales tax
  const totalWithTax = total + salesTax;

  return (
    <div>
      <Header />
      <div className="checkout-container">
        <h2>Your Order Details</h2>
        <div className="cart-items">
          {cart.map((item) => (
            <div key={item.id} className="cart-item">
              <img
                src={item.image}
                alt={item.name}
                className="cart-item-image"
              />
              <div className="cart-item-details">
                <h3>{item.name}</h3>
                <p>Price: ₹{item.price}</p>
                <p>Quantity: {item.quantity}</p>
              </div>
            </div>
          ))}
        </div>

        {/* <section className="bill"> */}
          <div className="delivery-address">
            <img src="images/delivery.png" alt="delivery location icon" />
            <div>
              <span>Delivery Address</span>
              <p>45, Green Street, Sector 12...</p>
            </div>
          </div>

          <div className="bill-summary">
            <div className="checkout-summary">
              <span>Items</span>
              <span> ₹{total}</span>
            </div>

            <p>
              <span>Sales Tax</span> <span>₹{salesTax}</span>
            </p>

            <p className="subtotal">
              <div className="checkout-summary">
                {/* <h3>Subtotal (items) ₹{total}</h3> */}
                <h3>Total (including tax) ₹{totalWithTax}</h3>
              </div>
            </p>
          </div>

          <Link to="/payment">
            <button className="checkout-button">Choose Payment Method</button>
          </Link>
        {/* </section> */}
      </div>

      {/* Similar Restaurants */}
      <section className="restaurants">
        <div className="bc">
          <h8>Similar Restaurants</h8>
          <div className="restaurants-cards">
            <div className="Deals">
              <img src="/images/mc logo.png" alt="Burgers & Fast Food" />
            </div>
            <div className="Deals">
              <img src="/images/papa logo.png " alt="Salads" />
            </div>
            <div className="Deals">
              <img src="/images/KFC logo.png" alt="Pasta & Casuals" />
            </div>
            <div className="Deals">
              <img src="/images/texac logo.png" alt="Pizza" />
            </div>
            <div className="Deals">
              <img src="/images/burger king logo.png" alt="Breakfast" />
            </div>
            <div className="Deals">
              <img src="/images/shaurma logo.png" alt="Soups" />
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default Checkout;





































