import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Header from "../header/header";
import Footer from "../footer/Footer";
import "./checkout.css";

const Checkout = () => {
  const { productId } = useParams();
  // const [cart, setCart] = useState([]);
  // const [total, setTotal] = useState(0);
  const [deliveryAddress, setDeliveryAddress] = useState("");
  // const [selectedAddress, setSelectedAddress] = useState(1); // Default to the first address

  // Dummy Address Data
  const addresses = [
    {
      id: 1,
      name: "Mike Ross",
      address: "45, Green Street, Sector 12, New Delhi, 110001, India",
      phone: "8734637468",
    },
    {
      id: 2,
      name: "Mike Ross",
      address: "Office 704, Tower B, Techno Plaza, Bengaluru, Karnataka, 560100",
      phone: "8937447362",
    },
  ];

  useEffect(() => {
    // Simulate fetching cart items
    const fetchCartItems = () => {
      const mockCart = [
        { id: 1, name: "Big Mac", image: "/images/bigmac.jpg", price: 120, quantity: 2 },
        { id: 2, name: "French Fries", image: "/images/fries.jpg", price: 50, quantity: 1 },
        { id: 3, name: "Chicken McNuggets", image: "/images/nuggets.jpg", price: 100, quantity: 1 },
      ];
      setCart(mockCart);
      const totalPrice = mockCart.reduce((acc, item) => acc + item.price * item.quantity, 0);
      setTotal(totalPrice);
    };

    fetchCartItems();
  }, []);

  const handleAddressChange = (e) => {
    setDeliveryAddress(e.target.value);
  };

  const handleCheckout = () => {
    alert(`Order placed! Delivery Address: ${deliveryAddress}`);
  };

  return (
    <div>
      <Header />

  <section class="bill">
    <img src="arrow-left (1).png" alt="arrow-left"/>
    <h2>Your Order Details</h2>
    <div class="order-details">
      <div class="items-section">
        <div class="item1">
          <img src="combo 1.png" alt="Royal Cheese Burger"/>
          <div class="item-info">
            <h3>Royal Cheese Burger</h3>
            <p>1x item</p>
          </div>
          <p class="price">₹120</p>
        </div>
        <div class="item">
          <img src="fries1.png" alt="Potato Veggies"/>
          <div class="item-info">
            <h3>Potato Veggies</h3>
            <p>1x item</p>
          </div>
          <p class="price">₹70</p>
        </div>
        <div class="item">
          <img src="cooldrink1.png" alt="Coke Coca Cola"/>
          <div class="item-info">
            <h3>Coke Coca Cola</h3>
            <p>1x item</p>
          </div>
          <p class="price">₹40</p>
        </div>
        <div class="notes">
          <label for="notes">Notes</label>
          <input id="notes" type="text" placeholder="Add order notes"/>
        </div>/
      </div>
      <div class="summary-section">
        <div class="delivery-address">
            <img src="Icon.png" alt="delivery location icon"/>
            <div>
                <span>Delivery Address</span>
                <p>45, Green Street, Sector 12...</p>
            </div>
        </div>
        <div class="bill-summary">
          <p><span>Items</span> <span>₹230</span></p>
          <p><span>Sales Tax</span> <span>₹10</span></p>
          <p class="subtotal"><span>Subtotal (3 items)</span> <span>₹240</span></p>
        </div>
        <button class="payment-btn">Choose Payment Method</button>
      </div>
    </div>
  </section>





































  {/* <!-- Popular Restaurants Section --> */}
  <section class="restaurants">
    <div class="bc">
      <h8>Similar Restaurants</h8>
      <div class="restaurants-cards">
        <div class="Deals">
          <img src="mc logo.png" alt="Burgers & Fast Food"/>
        </div>
        <div class="Deals">
          <img src="papa logo.png" alt="Salads"/>
        </div>
        <div class="Deals">
          <img src="KFC logo.png" alt="Pasta & Casuals"/>
        </div>
        <div class="Deals">
          <img src="texac logo.png" alt="Pizza"/>
        </div>
        <div class="Deals">
          <img src="burger king logo.png" alt="Breakfast"/>
        </div>
        <div class="Deals">
          <img src="shaurma logo.png" alt="Soups"/>
        </div>
      </div>
    </div>
  </section>

      <Footer />
    </div>
  );
};

export default Checkout;
