import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Header from "../header/header";
import Footer from "../footer/Footer";
import "./checkout.css";

const Checkout = () => {
  // const { productId } = useParams();
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  //   const [deliveryAddress, setDeliveryAddress] = useState("");
  // const [selectedAddress, setSelectedAddress] = useState(1); // Default to the first address

  // Dummy Address Data


  useEffect(() => {
    // Simulate fetching cart items
    const fetchCartItems = () => {
      const mockCart = [
        {
          id: 1,
          name: "Big Mac",
          image: "/images/bigmac.jpg",
          price: 120,
          quantity: 2,
        },
        {
          id: 2,
          name: "French Fries",
          image: "/images/fries.jpg",
          price: 50,
          quantity: 1,
        },
        {
          id: 3,
          name: "Chicken McNuggets",
          image: "/images/nuggets.jpg",
          price: 100,
          quantity: 1,
        },
      ];
      setCart(mockCart);
      const totalPrice = mockCart.reduce(
        (acc, item) => acc + item.price * item.quantity,
        0
      );
      setTotal(totalPrice);
    };

    fetchCartItems();
  }, []);

  //   const handleAddressChange = (e) => {
  //     setDeliveryAddress(e.target.value);
  //   };

  //   const handleCheckout = () => {
  //     alert(`Order placed! Delivery Address: ${deliveryAddress}`);
  //   };

  return (
    <div>
      <Header />
      <div className="checkout-container">
        <h2>Checkout</h2>
        <div className="cart-items">
          {cart.map((item) => (
            <div key={item.id} className="cart-item">
              <img
                src={item.image}
                alt={item.name}
                className="cart-item-image"
              />
              <div className="cart-item-details">
                <h3>{item.name}</h3>
                <p>Price: ${item.price}</p>
                <p>Quantity: {item.quantity}</p>
              </div>
            </div>
          ))}
        </div>
        <div className="checkout-summary">
          <h3>Total: ${total}</h3>
        </div>




  <section class="bill">
    <img src="arrow-left (1).png" alt="arrow-left"/>
    <h2>Your Order Details</h2>
    <div class="order-details">
      <div class="items-section">
        <div class="item1">
          <img src="combo 1.png" alt="Royal Cheese Burger"/>
          <div class="item-info">
            <h3>Royal Cheese Burger</h3>
            <p>1x item</p>
          </div>
          <p class="price">₹120</p>
        </div>
        <div class="item">
          <img src="fries1.png" alt="Potato Veggies"/>
          <div class="item-info">
            <h3>Potato Veggies</h3>
            <p>1x item</p>
          </div>
          <p class="price">₹70</p>
        </div>
        <div class="item">
          <img src="cooldrink1.png" alt="Coke Coca Cola"/>
          <div class="item-info">
            <h3>Coke Coca Cola</h3>
            <p>1x item</p>
          </div>
          <p class="price">₹40</p>
        </div>
        <div class="notes">
          <label for="notes">Notes</label>
          <input id="notes" type="text" placeholder="Add order notes"/>
        </div>
      </div>

      <div class="summary-section">
        <div class="delivery-address">
            <img src="Icon.png" alt="delivery location icon"/>
            <div>
                <span>Delivery Address</span>
                <p>45, Green Street, Sector 12...</p>
            </div>
        </div>
        <div class="bill-summary">
          <p><span>Items</span> <span>₹230</span></p>
          <p><span>Sales Tax</span> <span>₹10</span></p>
          <p class="subtotal"><span>Subtotal (3 items)</span> <span>₹240</span></p>
        </div>
        <button class="payment-btn">Choose Payment Method</button>
      </div>
    </div>
  </section>

        {/* <div className="delivery-address">
          <h3>Delivery Address</h3>
          <textarea
            value={deliveryAddress}
            onChange={handleAddressChange}
            placeholder="Enter your delivery address"
            rows="4"
          ></textarea>
        </div>
        <button onClick={handleCheckout} className="checkout-button">
          Place Order
        </button> */}
      </div>


      {/* Similar Restaurants */}
      <section className="restaurants">
        <div className="bc">
          <h8>Similar Restaurants</h8>
          <div className="restaurants-cards">
            <div className="Deals">
              <img src="/images/mc logo.png" alt="Burgers & Fast Food" />
            </div>
            <div class="Deals">
              <img src="/images/papa logo.png " alt="Salads" />
            </div>
            <div class="Deals">
              <img src="/images/KFC logo.png" alt="Pasta & Casuals" />
            </div>
            <div class="Deals">
              <img src="/images/texac logo.png" alt="Pizza" />
            </div>
            <div class="Deals">
              <img src="/images/burger king logo.png" alt="Breakfast" />
            </div>
            <div class="Deals">
              <img src="/images/shaurma logo.png" alt="Soups" />
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default Checkout;
