import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  const products = [
    { id: 1, name: "Product 1", price: 100 },
    { id: 2, name: "Product 2", price: 200 },
    { id: 3, name: "Product 3", price: 300 },
  ];

  // Add product to cart
  const addToCart = (product) => {
    setCart([...cart, product]);
    setTotal(total + product.price);
  };

  // Remove product from cart
  const removeFromCart = (productId) => {
    const updatedCart = cart.filter((item) => item.id !== productId);
    const removedProduct = cart.find((item) => item.id === productId);
    setCart(updatedCart);
    setTotal(total - removedProduct.price);
  };

  // Forward to checkout
  const forwardToCheckout = () => {
    alert("Forwarding to checkout...");
    // You can navigate to a checkout page here
  };

  return (
    <div className="container">
      <h1>Product Page</h1>
      <div className="products">
        {products.map((product) => (
          <div className="product" key={product.id}>
            <h3>{product.name}</h3>
            <p>Price: ${product.price}</p>
            <button onClick={() => addToCart(product)}>Add to Cart</button>
          </div>
        ))}
      </div>

      <div className="cart-container">
        <h2>Cart</h2>
        {cart.length === 0 ? (
          <p>Your cart is empty</p>
        ) : (
          <div>
            {cart.map((item) => (
              <div className="cart-item" key={item.id}>
                <p>
                  {item.name} - ${item.price}
                </p>
                <button onClick={() => removeFromCart(item.id)}>Remove</button>
              </div>
            ))}
          </div>
        )}
        <h3>Total: ${total}</h3>
        {cart.length > 0 && (
          <button className="checkout-btn" onClick={forwardToCheckout}>
            Checkout
          </button>
        )}
      </div>
    </div>
  );
};

export default App;
