                name="payment-method"
                value="Stripe"
                checked={selectedPaymentMethod === "Stripe"}
                readOnly
              />
            </div>

            {/* Add Debit Card Option */}
            <div className="payment-option add-card">
              <div className="payment-details">
                <div className="add-card-icon">+</div>
                <p>Add Debit Card</p>
              </div>
            </div>
          </div>

          {/* Payment Summary */}
          <div className="payment-summary">
