import React, { useState } from "react";
import ProductCard from "./ProductCard";
import Basket from "./Basket";

const Baskets = () => {
  const [basketItems, setBasketItems] = useState([]);
  const products = [
    { id: 1, name: "Cheese Burger", price: 150, description: "Delicious burger" },
    { id: 2, name: "Veggie Pizza", price: 200, description: "Loaded with veggies" },
  ];

  const handleAddToCart = (product) => {
    const itemIndex = basketItems.findIndex(item => item.id === product.id);
    if (itemIndex >= 0) {
      const updatedItems = [...basketItems];
      updatedItems[itemIndex].quantity += 1;
      setBasketItems(updatedItems);
    } else {
      setBasketItems([...basketItems, { ...product, quantity: 1 }]);
    }
  };

  return (
    <div>
      <h1>Available Products</h1>
      <div>
        {products.map(product => (
          <Product key={product.id} product={product} onAddToCart={handleAddToCart} />
        ))}
      </div>
      <Basket items={basketItems} />
    </div>
  );
};

export default Baskets;
