import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../header/header";
import Footer from "../footer/Footer";
import "./Checkout.css";

const Checkout = () => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [salesTax] = useState(10);

  useEffect(() => {
    // Simulate fetching cart items
    const fetchCartItems = () => {
      const mockCart = [
        {
          id: 1,
          name: "Royal Cheese Burger",
          image: "/images/combo-4.png",
          price: 120,
          quantity: 1,
        },
        {
          id: 2,
          name: "Potato Veggies",
          image: "/images/fries1.png",
          price: 70,
          quantity: 1,
        },
        {
          id: 3,
          name: "Coke Coca Cola",
          image: "/images/cooldrink1.png",
          price: 40,
          quantity: 1,
        },
      ];
      setCart(mockCart);
      const totalPrice = mockCart.reduce(
        (acc, item) => acc + item.price * item.quantity,
        0
      );
      setTotal(totalPrice);
    };

    fetchCartItems();
  }, []);

  // Calculate total price including sales tax
  const totalWithTax = total + salesTax;

  const navigate = useNavigate();

  const handleProceedPayment = () => {
    navigate("/payment", { state: { totalWithTax } });
  };

  return (
    <div>
      <Header />
      <div className="checkout-container">
        <div className="back-navigation">
          <img src="/images/arrow-left.png" alt="Go Back" />
          <h2>Your Order Details</h2>
        </div>

        <div className="cart-items">
          {cart.map((item) => (
            <div key={item.id} className="cart-item">
              <img
                src={item.image}
                alt={item.name}
                className="cart-item-image"
              />
              <div className="cart-item-details">
                <h3>{item.name}</h3>
                <p>1x item</p>
                <span>₹{item.price}</span>
              </div>
            </div>
          ))}
          <div className="notes">
            <input
              id="notes"
              type="text"
              placeholder="Add order notes"
            />
          </div>
        </div>

        <div className="delivery-address">
          <img src="/images/delivery.png" alt="Delivery Address Icon" />
          <div>
            <span>Delivery Address</span>
            <p>45, Green Street, Sector 12...</p>
          </div>
        </div>

        <div className="bill-summary">
          <div className="checkout-summary">
            <span>Items</span>
            <span>₹{total}</span>
          </div>
          <div className="checkout-summary">
            <span>Sales Tax</span>
            <span>₹{salesTax}</span>
          </div>
          <div className="checkout-summary subtotal">
            <h3>Subtotal (3 items)</h3>
            <h3>₹{totalWithTax}</h3>
          </div>
        </div>

        <button
          className="checkout-button"
          onClick={handleProceedPayment}
        >
          Choose Payment Method
        </button>
      </div>

      <Footer />
    </div>
  );
};

export default Checkout;




